import React from 'react'
import { Link } from 'react-router-dom'
import './Card.css'
const Card = ({item,item2}) => {
  if(item2){
   const src1='http://localhost:1337'+item.img;
   const price=item.price;
   const title=item.title

  }else{
    const src1='http://localhost:1337'+item.attributes.img.data.attributes.url;
    const src2='http://localhost:1337'+item.attributes.img2.data.attributes.url;
    const price=item.attributes.price;
    const title=item.attributes.title;
  }
 
  return (
    <Link to={`/product/${item.id}`} id='linkcard'>
        <div className='card'>
            <div className='image'>
                <img src={src1} alt='' className='mainImg' onMouseMove={(e)=>e.target.src=src2} onMouseLeave={(e)=>e.target.src=src1}/>
                
            </div>
            <h2>{item.attributes.title}</h2>
            <div className='prices'>
                <span>${item.attributes.price}</span>
                
            </div>
        </div>
    </Link>
  )
}

export default Card
