import React, { useState } from 'react'
import { useParams } from 'react-router-dom'
import List from '../components/List/List'
import useFetch from '../hooks/useFetch'
import './Products.css'
const Products = () => {
  const param=useParams()

 const id=parseInt(param.id)
  const[maxPrice,setMaxPrice]=useState(1000);
  const[maxPrice2,setMaxPrice2]=useState(1000);
  const[sort,setSort]=useState('asc'); 
  const[selectedSub,setSelectedSub]=useState([]);
  const {data,loading,error}=useFetch(`/sub-categories?[filters][categories][id][$eq]=${id}`)
  console.log(data)
  const change=(e)=>{
    const value=e.target.value;
    const checked=e.target.checked;
    setSelectedSub(checked 
      ? [...selectedSub,value]
      : selectedSub.filter((item)=>item!==value)

    )
  }
  console.log(selectedSub)
  function controlFromInput(fromSlider, fromInput, toInput, controlSlider) {
    const [from, to] = getParsed(fromInput, toInput);
    fillSlider(fromInput, toInput, '#C6C6C6', '#25daa5', controlSlider);
    if (from > to) {
        fromSlider.value = to;
        fromInput.value = to;
    } else {
        fromSlider.value = from;
    }
}
    
function controlToInput(toSlider, fromInput, toInput, controlSlider) {
    const [from, to] = getParsed(fromInput, toInput);
    fillSlider(fromInput, toInput, '#C6C6C6', '#25daa5', controlSlider);
    setToggleAccessible(toInput);
    if (from <= to) {
        toSlider.value = to;
        toInput.value = to;
    } else {
        toInput.value = from;
    }
}

function controlFromSlider(fromSlider, toSlider, fromInput) {
  const [from, to] = getParsed(fromSlider, toSlider);
  fillSlider(fromSlider, toSlider, '#C6C6C6', '#25daa5', toSlider);
  if (from > to) {
    fromSlider.value = to;
    fromInput.value = to;
  } else {
    fromInput.value = from;
  }
}

function controlToSlider(fromSlider, toSlider, toInput) {
  const [from, to] = getParsed(fromSlider, toSlider);
  fillSlider(fromSlider, toSlider, '#C6C6C6', '#25daa5', toSlider);
  setToggleAccessible(toSlider);
  if (from <= to) {
    toSlider.value = to;
    toInput.value = to;
  } else {
    toInput.value = from;
    toSlider.value = from;
  }
}

function getParsed(currentFrom, currentTo) {
  const from = parseInt(currentFrom.value, 10);
  const to = parseInt(currentTo.value, 10);
  return [from, to];
}

function fillSlider(from, to, sliderColor, rangeColor, controlSlider) {
    const rangeDistance = to.max-to.min;
    const fromPosition = from.value - to.min;
    const toPosition = to.value - to.min;
    controlSlider.style.background = `linear-gradient(
      to right,
      ${sliderColor} 0%,
      ${sliderColor} ${(fromPosition)/(rangeDistance)*100}%,
      ${rangeColor} ${((fromPosition)/(rangeDistance))*100}%,
      ${rangeColor} ${(toPosition)/(rangeDistance)*100}%, 
      ${sliderColor} ${(toPosition)/(rangeDistance)*100}%, 
      ${sliderColor} 100%)`;
}

function setToggleAccessible(currentTarget) {
  const toSlider = document.querySelector('#toSlider');
  if (Number(currentTarget.value) <= 0 ) {
    toSlider.style.zIndex = 2;
  } else {
    toSlider.style.zIndex = 0;
  }
}

const fromSlider = document.querySelector('#fromSlider');
const toSlider = document.querySelector('#toSlider');
const fromInput = document.querySelector('#fromInput');
const toInput = document.querySelector('#toInput');
fillSlider(fromSlider, toSlider, '#C6C6C6', '#25daa5', toSlider);
setToggleAccessible(toSlider);

fromSlider.oninput = () => controlFromSlider(fromSlider, toSlider, fromInput);
toSlider.oninput = () => controlToSlider(fromSlider, toSlider, toInput);
fromInput.oninput = () => controlFromInput(fromSlider, fromInput, toInput, toSlider);
toInput.oninput = () => controlToInput(toSlider, fromInput, toInput, toSlider);
  return (
    <div className='products'>
    <div className='left'>
    <div className='filterItem'>

     <h2>Product Categories</h2>
     {data?.map(item=>(

      <div className='inputItem'>
        <input className='checkb' type='checkbox' id={item.id} value={item.id} onChange={change}/>
        <label className='chdesc' htmlFor={item.id}>{item.attributes.title}</label>
      </div>

     ))}
      
  

    </div>
    <div className='filterItem'>
      <h2>Filter by price</h2>
      <div className='inputItem'>
      <div class="range_container">
    <div class="sliders_control">
       <input id="fromSlider" type="range" value="10" min="0" max="100"/>
       <input id="toSlider" type="range" value="30" min="0" max="100"/>
    </div>
    <div class="form_control">
      <div class="form_control_container">
          <div class="form_control_container__time">Min</div>
          <input class="form_control_container__time__input" type="number" id="fromInput" value="10" min="0" max="100"/>
        </div>
        <div class="form_control_container">
          <div class="form_control_container__time">Max</div>
          <input class="form_control_container__time__input" type="number" id="toInput" value="30" min="0" max="100"/>
        </div>
    </div>
</div>
        <button id='pricebnt' onClick={(e)=>setMaxPrice2(maxPrice)}>Save</button>
      </div>
    </div>
    <div className='filterItem'>
      <h2>Sort by</h2>
      <div className='inputItem'>
        <input type="radio" id='acs' value='asc' name="price" onChange={e=>setSort("asc")}/>
        <label htmlFor='asc' className='sortdesc'>Price (Lowest first)</label>
      </div>
      <div className='inputItem'>
        <input type="radio" id='desc' value='desc' name="price" onChange={e=>setSort("desc")}/>
        <label htmlFor='desc' className='sortdesc'>Price (Highest first)</label>
      </div>
    </div>
   
    </div>
    <div className='right'>
      <List id={id} maxPrice={maxPrice2} sort={sort} selectedSub={selectedSub}/>
    </div>
  </div>
  )
}

export default Products
